import { echo, exit, which } from 'shelljs';

const CONFIG_FILE_NAME = 'config';

export default {
  checkGit() {
    if (!which('git')) {
      echo('This script requires git');
      exit(1);
    }
  },

  checkNpm() {
    if (!which('npm')) {
      echo('This script requires npm');
      exit(1);
    }
  }
}
