import { exec } from 'shelljs';
import { install } from '../../config';

export default {
  brew() {
    (install.brew || []).forEach(brewPackage => exec(`brew install ${brewPackage}`));
    (install.cask || []).forEach(caskPackage => exec(`brew cask install ${caskPackage}`));
  },

  atom() {
    (install.apm || []).forEach(apmPackage => exec(`apm install ${apmPackage}`));
  },

  node() {
    (install.npm || []).forEach(npmPackage => exec(`npm install -g ${npmPackage}`));
  },
}
