import { exec } from 'shelljs';
import path from 'path';
import fs from 'fs';

const BASE_PATH = path.resolve('.');
const CONFIG_PATH = 'dots/setup';

export default {
  osx() {
    const fileName = '.osx';
    exec('softwareupdate -iva');
    exec(`. ${path.join(BASE_PATH, CONFIG_PATH, fileName)}`);
  },

  brew() {
    echo('Start brew setup script...');

    if (!which('brew')) {
      echo('Installing homebrew...');
      exec(`ruby -e '$(curl -fsSL https://raw.githubusercontent.com/Homebrew/install/master/install)'`)
    }

    exec('brew update');
    exec('brew upgrade --all');
    exec('brew tap caskroom/cask');
  },

  dev() {
    exec('xcode-select --install');
    exec('heroku update');

    // to launch start mongodb at login
    exec('ln -sfv /usr/local/opt/mongodb/*.plist ~/Library/LaunchAgents');

    // load mongodb now:
    exec('launchctl load ~/Library/LaunchAgents/homebrew.mxcl.mongodb.plist');

    exec(`nvm install`);
    exec(`rbenv install`);
    exec(`gem install bundler`);
  },
}
